mapper.select:
select

from ups_application


          and id = #{id,jdbcType=VARCHAR}


          and appName = #{appName,jdbcType=VARCHAR}


        and id = #{accountAppId,jdbcType=VARCHAR}

      and isAvailable = 1

select count(*)
from ups_application
where appName = #{appName,jdbcType=VARCHAR}

mapper.delete:
	  delete from ups_application


        and id = #{accountAppId,jdbcType=VARCHAR}

      and id = #{id,jdbcType=VARCHAR}
